#!/usr/bin/env node
import { parseArgs } from "util";
import { stop } from "../src/http-server.js";
async function teardown(host, port) {
    await stop(host, Number.parseInt(port, 10));
}
const options = {
    host: {
        short: "h",
        default: "localhost",
        type: "string",
    },
    port: {
        short: "p",
        default: "2117",
        type: "string",
    },
};
const parsed = parseArgs({ options });
teardown(parsed.values.host ?? options.host.default, parsed.values.port ?? options.port.default);
